    private void regSensorMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_regSensorMouseClicked
       // to load the fire alarm registration
       FireAlarmRegistration rgf = new FireAlarmRegistration();
       rgf.setVisible(true);
       rgf.pack();
       rgf.setLocationRelativeTo(null);
       rgf.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
       this.dispose();
    }//GEN-LAST:event_regSensorMouseClicked

